// then check if the result string is palindrome.
// A string is called palindrome if it reads the same backward as forward.
// You should return a tuple containing the result string and true/false for the check.
// Example
// >>> reverse_delete(("abcde"), ("ae"))
// (std::make_tuple("bcd", false))
// >>> reverse_delete(("abcdef"), ("b"))
// (std::make_tuple("acdef", false))
// >>> reverse_delete(("abcdedcba"), ("ab"))
// (std::make_tuple("cdedc", true))
std::tuple<std::string, bool> reverse_delete(std::string s, std::string c) {
    std::string result = "";
    for (int i = 0; i < s.length(); i++) {
        if (c.find(s[i]) == std::string::npos) {
            result += s[i];
        }
    }
    std::string reversed = result;
    std::reverse(reversed.begin(), reversed.end());
    bool is_palindrome = (result == reversed);
    return std::make_tuple(result, is_palindrome);

