// >>> even_odd_count((-12))
// (std::make_tuple(1, 1))
// >>> even_odd_count((123))
// (std::make_tuple(1, 2))
std::tuple<long, long> even_odd_count(long num) {
    long even_count = 0, odd_count = 0;
    if(num == 0) {
        even_count = 1;
    } else {
        while(num != 0) {
            int digit = num % 10;
            if(digit % 2 == 0) {
                even_count++;
            } else {
                odd_count++;
            }
            num /= 10;
        }
    }
    return std::make_tuple(even_count, odd_count);

