std::string find_max(std::vector<std::string> words) {
    std::string max_word = "";
    int max_unique = 0;
    for (auto word : words) {
        std::set<char> unique_chars(word.begin(), word.end());
        int num_unique = unique_chars.size();
        if (num_unique > max_unique || (num_unique == max_unique && word < max_word)) {
