// >>> solve(("1234"))
// ("4321")
// >>> solve(("ab"))
// ("AB")
// >>> solve(("#a@C"))
// ("#A@c")
std::string solve(std::string s) {
    bool has_letter = false;
    for (int i = 0; i < s.size(); i++) {
        if (isalpha(s[i])) {
            has_letter = true;
            if (islower(s[i])) {
                s[i] = toupper(s[i]);
            } else {
                s[i] = tolower(s[i]);
            }
        }
    }
    if (!has_letter) {
        std::reverse(s.begin(), s.end());
    }
    return s;

