    std::string temp = "";
    for (int i = 0; i < paren_string.length(); i++) {
        if (paren_string[i] == ' ') {
            continue;
        }
        if (paren_string[i] == '(') {
            st.push(paren_string[i]);
            temp += paren_string[i];
        } else if (paren_string[i] == ')') {
            if (st.empty()) {
                assert(false);
            }
            st.pop();
            temp += paren_string[i];
            if (st.empty()) {
                result.push_back(temp);
                temp = "";
            }
        }
    }
    if (!st.empty()) {
        assert(false);
    }
    return result;

