                encoded_message += tolower(message[i]);
            } else {
                encoded_message += toupper(message[i]);
            }
            if (vowels.find(message[i]) != std::string::npos) {
                int index = vowels.find(message[i]);
                int new_index = (index + 2) % vowels.length();
                encoded_message[i] = vowels[new_index];
