std::vector<long> count_up_to(long n) {
    std::vector<long> primes;
    for (long i = 2; i < n; i++) {
        bool is_prime = true;
        for (long j = 2; j * j <= i; j++) {
            if (i % j == 0) {
                is_prime = false;
                break;
            }
        }
        if (is_prime) {
            primes.push_back(i);
        }
    }
    return primes;

