// From a given vector of integers, generate a vector of rolling maximum element found until given moment
// in the sequence.
// >>> rolling_max((std::vector<long>({(long)1, (long)2, (long)3, (long)2, (long)3, (long)4, (long)2})))
// (std::vector<long>({(long)1, (long)2, (long)3, (long)3, (long)3, (long)4, (long)4}))
std::vector<long> rolling_max(std::vector<long> numbers) {
    std::vector<long> result;
    std::deque<long> dq;
    for (int i = 0; i < numbers.size(); i++) {
        while (!dq.empty() && dq.back() < numbers[i]) {
            dq.pop_back();
        }
        dq.push_back(numbers[i]);
        result.push_back(dq.front());
    }
    return result;
