//For a given number n, find the largest number that divides n evenly, smaller than n
// >>> largest_divisor(15)
// 5
function largest_divisor(n){
  for(let i=n-1; i>0; i--){
    if(n%i===0){
      return i;
    }
  }
}
//Return array of prime factors of given integer in the order from smallest to largest.
// Each of the factors should be arrayed number of times corresponding to how many times it appeares in factorization.
// Input number should be equal to the product of all factors
// >>> factorize(8)
// [2, 2, 2]
// >>> factorize(25)
// [5, 5]
// >>> factorize(70)
// [2, 5, 7]
function factorize(n){
  var factors = [];
  var divisor = 2;
  while(n > 1){
    while(n % divisor === 0){
      factors.push(divisor);
      n /= divisor;
    }
    divisor++;
  }
  return factors;
}
//From an array of integers, remove all elements that occur more than once.
// Keep order of elements left the same as in the input.
// >>> remove_duplicates([1, 2, 3, 2, 4])
// [1, 3, 4]
function remove_duplicates(numbers){
  let uniqueNumbers = [];
  numbers.forEach(number => {
    if (!uniqueNumbers.includes(number)) {
      uniqueNumbers.push(number);
    }
  });
  return uniqueNumbers;
}
//For a given string, flip lowercase characters to uppercase and uppercase to lowercase.
// >>> flip_case("Hello")
// "hELLO"
function flip_case(string){
  let flippedString = "";
  for(let i = 0; i < string.length; i++){
    if(string[i] === string[i].toUpperCase()){
      flippedString += string[i].toLowerCase();
    } else {
      flippedString += string[i].toUpperCase();
    }
  }
  return flippedString;
}
//Concatenate array of strings into a single string
// >>> concatenate([])
// ""
// >>> concatenate(["a", "b", "c"])
// "abc"
function concatenate(strings){
  return strings.join("");
}
//Filter an input array of strings only for ones that start with a given prefix.
// >>> filter_by_prefix([], "a")
// []
// >>> filter_by_prefix(["abc", "bcd", "cde", "array"], "a")
// ["abc", "array"]
function filter_by_prefix(strings, prefix){
  return strings.filter(function(string){
    return string.startsWith(prefix);
  });
}
//Given a positive floating point number, it can be decomposed into
// and integer part (largest integer smaller than given number) and decimals
// (leftover part always smaller than 1).
// Return the decimal part of the number.
// >>> truncate_number(3.5)
// 0.5
function truncate_number(number){
  var integer_part = Math.floor(number);
  var decimal_part = number - integer_part;
  return decimal_part;
}
//Return only positive numbers in the array.
// >>> get_positive([-1, 2, -4, 5, 6])
// [2, 5, 6]
// >>> get_positive([5, 3, -5, 2, -3, 3, 9, 0, 123, 1, -10])
// [5, 3, 2, 3, 9, 123, 1]
function get_positive(l){
  return l.filter(function(x){
    return x > 0;
  });
}
//Return true if a given number is prime, and false otherwise.
// >>> is_prime(6)
// false
// >>> is_prime(101)
// true
// >>> is_prime(11)
// true
// >>> is_prime(13441)
// true
// >>> is_prime(61)
// true
// >>> is_prime(4)
// false
// >>> is_prime(1)
// false
function is_prime(n){
  if(n < 2) return false;
  for(let i = 2; i <= Math.sqrt(n); i++){
    if(n % i === 0) return false;
  }
  return true;
}
//This function takes an array l and returns an array l' such that
// l' is identical to l in the indicies that are not divisible by three, while its values at the indicies that are divisible by three are equal
// to the values of the corresponding indicies of l, but sorted.
// >>> sort_third([1, 2, 3])
// [1, 2, 3]
// >>> sort_third([5, 6, 3, 4, 8, 9, 2])
// [2, 6, 3, 4, 8, 9, 5]
function sort_third(l){
  let l_prime = [];
  let third_values = [];
  for(let i=0; i<l.length; i++){
    if(i%3===0){
      third_values.push(l[i]);
    }
    else{
      l_prime.push(l[i]);
    }
  }
  third_values.sort();
  let j=0;
  for(let i=0; i<l.length; i++){
    if(i%3===0){
      l_prime.splice(i, 0, third_values[j]);
      j++;
    }
  }
  return l_prime;
}
//Return sorted unique elements in an array
// >>> unique([5, 3, 5, 2, 3, 3, 9, 0, 123])
// [0, 2, 3, 5, 9, 123]
function unique(l){
  return l.filter((v, i, a) => a.indexOf(v) === i).sort((a, b) => a - b);
}
//Return maximum element in the array.
// >>> max_element([1, 2, 3])
// 3
// >>> max_element([5, 3, -5, 2, -3, 3, 9, 0, 123, 1, -10])
// 123
function max_element(l){
    return Math.max(...l);
}
//Return the number of times the digit 7 appears in integers less than n which are divisible by 11 or 13.
// >>> fizz_buzz(50)
// 0
// >>> fizz_buzz(78)
// 2
// >>> fizz_buzz(79)
// 3
function fizz_buzz(n){
  let count = 0;
  for(let i = 1; i < n; i++){
    if(i % 11 === 0 || i % 13 === 0){
      let str = i.toString();
      let num = str.split('').filter(x => x === '7').length;
      count += num;
    }
  }
  return count;
}
//This function takes an array l and returns an array l' such that
// l' is identical to l in the odd indicies, while its values at the even indicies are equal
// to the values of the even indicies of l, but sorted.
// >>> sort_even([1, 2, 3])
// [1, 2, 3]
// >>> sort_even([5, 6, 3, 4])
// [3, 6, 5, 4]
function sort_even(l){
  let even_values = l.filter((value, index) => index % 2 === 0).sort((a, b) => a - b);
  let result = [];
  for(let i = 0; i < l.length; i++){
    if(i % 2 === 0){
      result.push(even_values.shift());
    } else {
      result.push(l[i]);
    }
  }
  return result;
}
//prime_fib returns n-th number that is a Fibonacci number and it's also prime.
// >>> prime_fib(1)
// 2
// >>> prime_fib(2)
// 3
// >>> prime_fib(3)
// 5
// >>> prime_fib(4)
// 13
// >>> prime_fib(5)
// 89
function prime_fib(n){
  let fib = [0, 1];
  let i = 2;
  while (fib.length < n + 1) {
    fib[i] = fib[i - 1] + fib[i - 2];
    i++;
  }
  let prime = fib.filter(num => {
    for (let i = 2; i < num; i++) {
      if (num % i === 0) return false;
    }
    return num > 1;
  });
  return prime[n - 1];
}
//You're given an array of deposit and withdrawal operations on a bank account that starts with
// zero balance. Your task is to detect if at any point the balance of account fallls below zero, and
// at that point function should return true. Otherwise it should return false.
// >>> below_zero([1, 2, 3])
// false
// >>> below_zero([1, 2, -4, 5])
// true
function below_zero(operations){
  let balance = 0;
  for(let i=0; i<operations.length; i++){
    balance += operations[i];
    if(balance < 0){
      return true;
    }
  }
  return false;
}
//triples_sum_to_zero takes an array of integers as an input.
// it returns true if there are three distinct elements in the array that
// sum to zero, and false otherwise.
// >>> triples_sum_to_zero([1, 3, 5, 0])
// false
// >>> triples_sum_to_zero([1, 3, -2, 1])
// true
// >>> triples_sum_to_zero([1, 2, 3, 7])
// false
// >>> triples_sum_to_zero([2, 4, -5, 3, 9, 7])
// true
// >>> triples_sum_to_zero([1])
// false
function triples_sum_to_zero(l){
  for(let i=0; i<l.length-2; i++){
    for(let j=i+1; j<l.length-1; j++){
      for(let k=j+1; k<l.length; k++){
