// >>> median([-10, 4, 6, 1000, 10, 20])
// 15.0
function median(l){
    l.sort(function(a, b){return a - b});
    var len = l.length;
    if(len % 2 === 0){
        return (l[len/2 - 1] + l[len/2]) / 2;
    } else {
        return l[Math.floor(len/2)];
    }
}
