//For a given array of input numbers, calculate Mean Absolute Deviation
// around the mean of this dataset.
// Mean Absolute Deviation is the average absolute difference between each
// element and a centerpoint (mean in this case):
// MAD = average | x - x_mean |
// >>> mean_absolute_deviation([1.0, 2.0, 3.0, 4.0])
// 1.0
function mean_absolute_deviation(numbers){
  //Calculate the mean of the input numbers
  let mean = numbers.reduce((a, b) => a + b, 0) / numbers.length;

  //Calculate the absolute difference between each element and the mean
  let absolute_differences = numbers.map(number => Math.abs(number - mean));

  //Calculate the mean of the absolute differences
  let mad = absolute_differences.reduce((a, b) => a + b, 0) / absolute_differences.length;

  return mad;
}
