// >>> correct_bracketing("<")
// false
// >>> correct_bracketing("<>")
// true
// >>> correct_bracketing("<<><>>")
// true
// >>> correct_bracketing("><<>")
// false
function correct_bracketing(brackets){
  let stack = [];
  for(let i=0; i<brackets.length; i++){
    if(brackets[i] === "<"){
      stack.push("<");
    }
    else if(brackets[i] === ">"){
      if(stack.length === 0){
        return false;
      }
      stack.pop();
    }
  }
  return stack.length === 0;
}
//Return true is array elements are monotonically increasing or decreasing.
// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l){
  let increasing = true;
  let decreasing = true;
  for(let i=0; i<l.length-1; i++){
    if(l[i] > l[i+1]){
      increasing = false;
    }
    if(l[i] < l[i+1]){
      decreasing = false;
    }
  }
  return increasing || decreasing;
}
