// [2, 3]
function common(l1, l2){
  let set1 = new Set(l1);
  let set2 = new Set(l2);
  let result = [];
  for(let item of set1){
    if(set2.has(item)){
      result.push(item);
    }
  }
  return result.sort((a, b) => a - b);
}
