// >>> fibfib(1)
// 0
// >>> fibfib(5)
// 4
// >>> fibfib(8)
// 24
function fibfib(n){
  if(n === 0 || n === 1){
    return 0;
  }
  if(n === 2){
    return 1;
  }
  let a = 0;
  let b = 0;
  let c = 1;
  for(let i = 3; i <= n; i++){
    let temp = a + b + c;
    a = b;
    b = c;
    c = temp;
  }
  return c;
}
