// If no such a value exist, return -1.
// Examples:
// >>> search([4, 1, 2, 2, 3, 1])
// 2
// >>> search([1, 2, 2, 3, 3, 3, 4, 4, 4])
// 3
// >>> search([5, 5, 4, 4, 4])
// -1
function search(lst){
    let freq = {};
    for(let i=0; i<lst.length; i++){
        if(freq[lst[i]]){
            freq[lst[i]] += 1;
        } else {
            freq[lst[i]] = 1;
        }
    }
    let max = -1;
    for(let key in freq){
        if(parseInt(key) > max && freq[key] >= parseInt(key)){
            max = parseInt(key);
        }
    }
    return max;
}
//Input to this function is a string represented multiple groups for nested parentheses separated by spaces.
// For each of the group, output the deepest level of nesting of parentheses.
// E.g. (()()) has maximum two levels of nesting while ((())) has three.
// >>> parse_nested_parens("(()()) ((())) () ((())()())")
// [2, 3, 1, 3]
function parse_nested_parens(paren_string){
  let groups = paren_string.split(" ");
  let max_depths = [];
  for(let i=0; i<groups.length; i++){
    let depth = 0;
    let max_depth = 0;
    for(let j=0; j<groups[i].length; j++){
      if(groups[i][j] == "("){
        depth++;
        if(depth > max_depth){
          max_depth = depth;
        }
      }
      else if(groups[i][j] == ")"){
        depth--;
      }
    }
    max_depths.push(max_depth);
  }
  return max_depths;
}
