// >>> strange_sort_list([5, 5, 5, 5])
// [5, 5, 5, 5]
// >>> strange_sort_list([])
// []
function strange_sort_list(lst){
  let sorted = lst.sort((a, b) => a - b);
  let result = [];
  while(sorted.length > 0){
    result.push(sorted.shift());
    if(sorted.length > 0){
      result.push(sorted.pop());
    }
  }
